INSERT INTO `companies` (`id`, `name`, `description`, `region_id`, `company_id`) VALUES
	(0, 'Valve', 'Американская компания-разработчик компьютерных игр, создавшая такие серии игр как: Half-Life, Portal, Counter-Strike, Team Fortress, Day of Defeat, Left 4 Dead, Ricochet, Alien Swarm, Dota 2, Artifact и Dota Underlords; игровые движки: GoldSrc, Source, Source 2; программное обеспечение: Steam, Valve Anti-Cheat, SteamOS, Steam Audio; а также аппаратное обеспечение: Steam Machines, Steam Controller, Valve Index. Основана 24 августа 1996 года бывшими сотрудниками Microsoft Гейбом Ньюэллом и Майком Харрингтоном.', 2, NULL),
	(1, 'Nintendo', 'Nintendo (яп. 任天堂株式会社 Нинтэндо: кабусики-гайся) — японская компания, специализирующаяся на создании видеоигр и игровых консолей.', NULL, NULL),
	(2, '343 Industries', '343 Industries (сокр. 343i) — американская компания, занимающаяся разработкой компьютерных игр серии Halo с 2007 года. Студия была названа в честь персонажа 343 Виновная Искра из серии Halo. Компания была открыта в 2007 году благодаря Microsoft Studios для того, чтобы поддерживать производство контента серии Halo после студии Bungie, у которых к тому моменту нарастали противоречия с Microsoft. Halo 4 стала первой самостоятельной игрой выпущенной из под пера 343i.', 2, 4),
	(3, 'CD Projekt RED', 'Компания-разработчик компьютерных игр, расположенная в Польше.', NULL, NULL),
	(4, 'Xbox Game Studios', 'Дочерняя компания Microsoft, разрабатывающая и издающая компьютерные игры для игровых консолей серии Xbox и персональных компьютеров под управлением Windows. Xbox Game Studios издаёт как игры собственной разработки, например, Rare и 343 Industries, так и игры сторонних фирм — Epic Games и Remedy Entertainment.', 2, NULL),
	(5, 'Rockstar Games', 'Rockstar Games, Inc. — американская компания, специализирующаяся на разработке и издании компьютерных игр. Владельцем Rockstar Games является корпорация Take-Two Interactive. Бренд компании широко известен по сериям игр Grand Theft Auto, Midnight Club, Max Payne, Manhunt и Red Dead. Главный офис компании расположен в Нью-Йорке.', 2, NULL),
