INSERT INTO `engines` (`id`, `name`, `description`, `company_id`) VALUES
	(0, 'Unity 3D', 'Игровой движок, наиболее популярный среди инди-разработчиков. Его достаточно сложно освоить, но в этом помогают большое количество гайдов, документации и видеоуроков. Основной язык программирования — C#, но имеющийся функционал позволяет создавать прототипы, не написав ни единой строчки кода. Встроенный магазин ассетов содержит десятки тысяч платных и бесплатных моделей, шейдеров и прочих готовых ресурсов, что экономит время.', NULL),
	(1, 'Unreal Engine 4', 'Игровой движок, разрабатываемый и поддерживаемый компанией Epic Games. Первой игрой на этом движке был шутер от первого лица Unreal, выпущенный в 1998 году. Хотя движок первоначально был предназначен для разработки шутеров от первого лица, его последующие версии успешно применялись в играх самых различных жанров, в том числе стелс-играх, файтингах и массовых многопользовательских ролевых онлайн-играх.', 14);
