INSERT INTO `platforms` (`id`, `name`, `description`, `year_release`, `sales_worldwide`, `generation`, `company_id`, `region_id`) VALUES
	(0, 'Xbox one', 'Третья по счёту игровая приставка от компании Microsoft, являющаяся преемницей Xbox 360. Конкурентами Xbox One среди игровых платформ восьмого поколения являются PlayStation 4 от Sony Computer Entertainment, Wii U и Nintendo Switch от Nintendo. ', 2013, 50000000, 8, NULL, 2),
	(1, 'PlayStation 4', 'Игровая приставка восьмого поколения, выпускаемая японской компанией Sony. PlayStation 4 входит в семейство игровых приставок PlayStation и была выпущена на смену PlayStation 3 — предыдущей домашней игровой консоли той же компании. Официальный анонс PlayStation 4 состоялся на конференции PlayStation Meeting 2013 в феврале 2013 года, продажи начались в ноябре 2013 года в США, Европе, Южной Америке, России и Австралии и в феврале 2014 года в Японии[7]. Осенью 2016 года Sony выпустила две новые аппаратные версии приставки: PlayStation 4 Slim, обладающую уменьшенными габаритами, и PlayStation 4 Pro, оснащённую более мощными основным и графическим процессором с расчётом на возможность вывода изображения и потокового видео в разрешении 4K. ', 2013, 100000000, 8, NULL, 2),
	(2, 'PC', NULL, NULL, NULL, NULL, NULL, NULL);
